import React, { useState, useEffect, useRef } from 'react';
import { Container, Grid, Paper, Typography, TextField, Box, Button, Select, MenuItem, FormControl, InputLabel } from '@mui/material';
import { Bar, Line } from 'react-chartjs-2';
import html2canvas from 'html2canvas';
import jsPDF from 'jspdf';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  Title,
  Tooltip,
  Legend
);

function App() {
  const [inputs, setInputs] = useState({
    평단가: 3000,
    일매출수: 50,
    원재료비: 800,
    월관리비: 340000,
    상가보증금: 30000000,
    대출이자: 5,
    투자비: 65000000,
  });

  const [scenarios, setScenarios] = useState({
    case2: 10,
    case3: 20,
  });

  const [results, setResults] = useState({
    case1: {},
    case2: {},
    case3: {},
  });

  const contentRef = useRef(null);

  const formatNumber = (num) => {
    return num.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
  };

  const calculateResults = () => {
    // Case 1 (기본)
    const case1 = {
      월매출: inputs.평단가 * ((inputs.일매출수 * 22) + (inputs.일매출수 * 1.1 * 8)),
      월총지출: (inputs.원재료비 * ((inputs.일매출수 * 22) + (inputs.일매출수 * 1.1 * 8))) +
        inputs.월관리비 + (inputs.상가보증금 * (inputs.대출이자 / 100) / 12),
    };

    // Case 2 (사용자 지정 % 증가)
    const case2 = {
      월매출: inputs.평단가 * ((inputs.일매출수 * (1 + scenarios.case2/100) * 22) + 
        (inputs.일매출수 * (1 + scenarios.case2/100) * 1.1 * 8)),
      월총지출: (inputs.원재료비 * ((inputs.일매출수 * (1 + scenarios.case2/100) * 22) + 
        (inputs.일매출수 * (1 + scenarios.case2/100) * 1.1 * 8))) +
        inputs.월관리비 + (inputs.상가보증금 * (inputs.대출이자 / 100) / 12),
    };

    // Case 3 (사용자 지정 % 증가)
    const case3 = {
      월매출: inputs.평단가 * ((inputs.일매출수 * (1 + scenarios.case3/100) * 22) + 
        (inputs.일매출수 * (1 + scenarios.case3/100) * 1.1 * 8)),
      월총지출: (inputs.원재료비 * ((inputs.일매출수 * (1 + scenarios.case3/100) * 22) + 
        (inputs.일매출수 * (1 + scenarios.case3/100) * 1.1 * 8))) +
        inputs.월관리비 + (inputs.상가보증금 * (inputs.대출이자 / 100) / 12),
    };

    setResults({ case1, case2, case3 });
  };

  useEffect(() => {
    calculateResults();
  }, [inputs, scenarios]);

  const handleInputChange = (field) => (event) => {
    const value = event.target.value === '' ? '' : Number(event.target.value);
    setInputs({
      ...inputs,
      [field]: value,
    });
  };

  const handleScenarioChange = (field) => (event) => {
    setScenarios({
      ...scenarios,
      [field]: Number(event.target.value),
    });
  };

  const exportToPDF = async () => {
    if (contentRef.current) {
      const canvas = await html2canvas(contentRef.current);
      const imgData = canvas.toDataURL('image/png');
      const pdf = new jsPDF('p', 'mm', 'a4');
      const pdfWidth = pdf.internal.pageSize.getWidth();
      const pdfHeight = (canvas.height * pdfWidth) / canvas.width;
      
      pdf.addImage(imgData, 'PNG', 0, 0, pdfWidth, pdfHeight);
      pdf.save('비즈니스_수익성_분석.pdf');
    }
  };

  const barChartData = {
    labels: ['기본', `${scenarios.case2}% 증가`, `${scenarios.case3}% 증가`],
    datasets: [
      {
        label: '월 매출',
        data: [results.case1.월매출, results.case2.월매출, results.case3.월매출],
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
      },
      {
        label: '월 총 지출',
        data: [results.case1.월총지출, results.case2.월총지출, results.case3.월총지출],
        backgroundColor: 'rgba(255, 99, 132, 0.6)',
      },
    ],
  };

  const lineChartData = {
    labels: ['기본', `${scenarios.case2}% 증가`, `${scenarios.case3}% 증가`],
    datasets: [
      {
        label: '월 순이익',
        data: [
          results.case1.월매출 - results.case1.월총지출,
          results.case2.월매출 - results.case2.월총지출,
          results.case3.월매출 - results.case3.월총지출,
        ],
        borderColor: 'rgb(75, 192, 192)',
        tension: 0.1,
      },
    ],
  };

  const combinedChartData = {
    labels: ['기본', `${scenarios.case2}% 증가`, `${scenarios.case3}% 증가`],
    datasets: [
      {
        type: 'bar',
        label: '월 매출',
        data: [results.case1.월매출, results.case2.월매출, results.case3.월매출],
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
        yAxisID: 'y',
      },
      {
        type: 'bar',
        label: '월 총 지출',
        data: [results.case1.월총지출, results.case2.월총지출, results.case3.월총지출],
        backgroundColor: 'rgba(255, 99, 132, 0.6)',
        yAxisID: 'y',
      },
      {
        type: 'line',
        label: '월 순이익',
        data: [
          results.case1.월매출 - results.case1.월총지출,
          results.case2.월매출 - results.case2.월총지출,
          results.case3.월매출 - results.case3.월총지출,
        ],
        borderColor: 'rgb(153, 102, 255)',
        tension: 0.1,
        yAxisID: 'y1',
      },
    ],
  };

  const renderInputSection = (title, fields) => (
    <Paper sx={{ p: 2, mb: 2 }}>
      <Typography variant="h6" gutterBottom>
        {title}
      </Typography>
      <Grid container spacing={2}>
        {fields.map((field) => (
          <Grid item xs={12} sm={6} key={field}>
            <TextField
              fullWidth
              label={field}
              type="number"
              value={inputs[field]}
              onChange={handleInputChange(field)}
              variant="outlined"
              size="small"
              inputProps={{ inputMode: 'numeric', pattern: '[0-9]*' }}
              InputProps={{
                endAdornment: field === '대출이자' ? '%' : null,
                readOnly: false,
              }}
              InputLabelProps={{
                shrink: true,
              }}
              FormHelperTextProps={{
                sx: { textAlign: 'right' }
              }}
              helperText={field !== '대출이자' ? formatNumber(inputs[field]) : null}
            />
          </Grid>
        ))}
      </Grid>
    </Paper>
  );

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Typography variant="h4" component="h1" gutterBottom align="center">
        비즈니스 수익성 분석
      </Typography>

      <Box sx={{ mb: 2, textAlign: 'right' }}>
        <Button variant="contained" color="primary" onClick={exportToPDF}>
          PDF로 저장
        </Button>
      </Box>

      <div ref={contentRef}>
        <Grid container spacing={3}>
          <Grid item xs={12} md={6}>
            <Paper sx={{ p: 2, mb: 2 }}>
              <Typography variant="h6" gutterBottom>
                매출 증대 시나리오
              </Typography>
              <Grid container spacing={2}>
                <Grid item xs={12} sm={6}>
                  <FormControl fullWidth size="small">
                    <InputLabel>Case 2 증가율</InputLabel>
                    <Select
                      value={scenarios.case2}
                      onChange={handleScenarioChange('case2')}
                      label="Case 2 증가율"
                    >
                      <MenuItem value={5}>5%</MenuItem>
                      <MenuItem value={10}>10%</MenuItem>
                      <MenuItem value={15}>15%</MenuItem>
                      <MenuItem value={20}>20%</MenuItem>
                      <MenuItem value={25}>25%</MenuItem>
                      <MenuItem value={30}>30%</MenuItem>
                    </Select>
                  </FormControl>
                </Grid>
                <Grid item xs={12} sm={6}>
                  <FormControl fullWidth size="small">
                    <InputLabel>Case 3 증가율</InputLabel>
                    <Select
                      value={scenarios.case3}
                      onChange={handleScenarioChange('case3')}
                      label="Case 3 증가율"
                    >
                      <MenuItem value={5}>5%</MenuItem>
                      <MenuItem value={10}>10%</MenuItem>
                      <MenuItem value={15}>15%</MenuItem>
                      <MenuItem value={20}>20%</MenuItem>
                      <MenuItem value={25}>25%</MenuItem>
                      <MenuItem value={30}>30%</MenuItem>
                    </Select>
                  </FormControl>
                </Grid>
              </Grid>
            </Paper>

            {renderInputSection('매출 입력값', ['평단가', '일매출수', '원재료비'])}
            {renderInputSection('월관리비', ['월관리비'])}
            {renderInputSection('상가임대료', ['상가보증금', '대출이자'])}
            {renderInputSection('투자비', ['투자비'])}
          </Grid>

          <Grid item xs={12} md={6}>
            <Paper sx={{ p: 2 }}>
              <Typography variant="h6" gutterBottom>
                결과
              </Typography>
              <Box sx={{ mb: 2 }}>
                <Typography variant="subtitle1">월 매출 및 지출 비교</Typography>
                <Bar data={barChartData} />
              </Box>
              <Box sx={{ mb: 2 }}>
                <Typography variant="subtitle1">월 순이익 추이</Typography>
                <Line data={lineChartData} />
              </Box>
              <Box>
                <Typography variant="subtitle1">통합 분석</Typography>
                <Bar data={combinedChartData} />
              </Box>
            </Paper>
          </Grid>

          <Grid item xs={12}>
            <Paper sx={{ p: 2 }}>
              <Typography variant="h6" gutterBottom>
                투자 회수 기간
              </Typography>
              <Grid container spacing={2}>
                {['case1', 'case2', 'case3'].map((case_, index) => {
                  const monthlyProfit = results[case_].월매출 - results[case_].월총지출;
                  const monthsToRecover = inputs.투자비 / monthlyProfit;
                  const years = Math.floor(monthsToRecover / 12);
                  const months = Math.round(monthsToRecover % 12);

                  return (
                    <Grid item xs={12} sm={4} key={case_}>
                      <Paper sx={{ p: 2, bgcolor: 'background.default' }}>
                        <Typography variant="subtitle1">
                          {index === 0 ? '기본' : `${scenarios[case_]}% 증가`}
                        </Typography>
                        <Typography>
                          {years}년 {months}개월
                        </Typography>
                      </Paper>
                    </Grid>
                  );
                })}
              </Grid>
            </Paper>
          </Grid>
        </Grid>
      </div>
    </Container>
  );
}

export default App;
