function App() {
  const [inputs, setInputs] = useState({
    ÌèâÎã®Í∞Ä: 3000,
    ÏùºÎß§Ï∂úÏàò: 50,
    ÏõêÏû¨Î£åÎπÑ: 1000,
    ÏõîÍ¥ÄÎ¶¨ÎπÑ: 2500000,
    ÏÉÅÍ∞ÄÎ≥¥Ï¶ùÍ∏à: 30000000,
    ÎåÄÏ∂úÏù¥Ïûê: 5,
    Ìà¨ÏûêÎπÑ: 50000000,
