  const getEmoji = (years, months) => {
    const totalMonths = years * 12 + months;
    const targetMonths = 24; // 2ÎÖÑ
    const threshold = targetMonths * 0.2; // 20% Í∏∞Ï§Ä

    if (totalMonths < targetMonths) {
      return 'üòÑ'; // 2ÎÖÑ ÎØ∏Îßå
