      return 'üôÇ'; // 2ÎÖÑ ¬±20% ÎÇ¥
    } else {
      return 'üò¢'; // 2ÎÖÑÎ≥¥Îã§ 20% Ï¥àÍ≥º
    }
  };

  const calculateResults = () => {
    // Case 1 (Í∏∞Î≥∏)
    const case1 = {
      ÏõîÎß§Ï∂ú: inputs.ÌèâÎã®Í∞Ä * ((inputs.ÏùºÎß§Ï∂úÏàò * 22) + (inputs.ÏùºÎß§Ï∂úÏàò * 1.1 * 8)),
      ÏõîÏ¥ùÏßÄÏ∂ú: (inputs.ÏõêÏû¨Î£åÎπÑ * ((inputs.ÏùºÎß§Ï∂úÏàò * 22) + (inputs.ÏùºÎß§Ï∂úÏàò * 1.1 * 8))) +
        inputs.ÏõîÍ¥ÄÎ¶¨ÎπÑ + (inputs.ÏÉÅÍ∞ÄÎ≥¥Ï¶ùÍ∏à * (inputs.ÎåÄÏ∂úÏù¥Ïûê / 100) / 12),
    };

    // Case 2 (ÏÇ¨Ïö©Ïûê ÏßÄÏ†ï % Ï¶ùÍ∞Ä)
    const case2 = {
      ÏõîÎß§Ï∂ú: inputs.ÌèâÎã®Í∞Ä * ((inputs.ÏùºÎß§Ï∂úÏàò * (1 + scenarios.case2/100) * 22) + 
        (inputs.ÏùºÎß§Ï∂úÏàò * (1 + scenarios.case2/100) * 1.1 * 8)),
      ÏõîÏ¥ùÏßÄÏ∂ú: (inputs.ÏõêÏû¨Î£åÎπÑ * ((inputs.ÏùºÎß§Ï∂úÏàò * (1 + scenarios.case2/100) * 22) + 
        (inputs.ÏùºÎß§Ï∂úÏàò * (1 + scenarios.case2/100) * 1.1 * 8))) +
        inputs.ÏõîÍ¥ÄÎ¶¨ÎπÑ + (inputs.ÏÉÅÍ∞ÄÎ≥¥Ï¶ùÍ∏à * (inputs.ÎåÄÏ∂úÏù¥Ïûê / 100) / 12),
    };

    // Case 3 (ÏÇ¨Ïö©Ïûê ÏßÄÏ†ï % Ï¶ùÍ∞Ä)
    const case3 = {
      ÏõîÎß§Ï∂ú: inputs.ÌèâÎã®Í∞Ä * ((inputs.ÏùºÎß§Ï∂úÏàò * (1 + scenarios.case3/100) * 22) + 
        (inputs.ÏùºÎß§Ï∂úÏàò * (1 + scenarios.case3/100) * 1.1 * 8)),
      ÏõîÏ¥ùÏßÄÏ∂ú: (inputs.ÏõêÏû¨Î£åÎπÑ * ((inputs.ÏùºÎß§Ï∂úÏàò * (1 + scenarios.case3/100) * 22) + 
        (inputs.ÏùºÎß§Ï∂úÏàò * (1 + scenarios.case3/100) * 1.1 * 8))) +
        inputs.ÏõîÍ¥ÄÎ¶¨ÎπÑ + (inputs.ÏÉÅÍ∞ÄÎ≥¥Ï¶ùÍ∏à * (inputs.ÎåÄÏ∂úÏù¥Ïûê / 100) / 12),
    };

    setResults({ case1, case2, case3 });
  };

  useEffect(() => {
    calculateResults();
  }, [inputs, scenarios]);

  const handleInputChange = (field) => (event) => {
    const value = event.target.value === '' ? '' : Number(event.target.value);
    setInputs({
      ...inputs,
      [field]: value,
    });
  };

  const handleScenarioChange = (field) => (event, newValue) => {
    setScenarios({
      ...scenarios,
      [field]: newValue,
    });
  };

  const exportToPDF = async () => {
    if (contentRef.current) {
      const canvas = await html2canvas(contentRef.current);
      const imgData = canvas.toDataURL('image/png');
      const pdf = new jsPDF('p', 'mm', 'a4');
      const pdfWidth = pdf.internal.pageSize.getWidth();
      const pdfHeight = (canvas.height * pdfWidth) / canvas.width;
      
      pdf.addImage(imgData, 'PNG', 0, 0, pdfWidth, pdfHeight);
      pdf.save('ÎπÑÏ¶àÎãàÏä§_ÏàòÏùµÏÑ±_Î∂ÑÏÑù.pdf');
    }
  };

  const barChartData = {
    labels: ['Í∏∞Î≥∏', `${scenarios.case2}% Ï¶ùÍ∞Ä`, `${scenarios.case3}% Ï¶ùÍ∞Ä`],
    datasets: [
      {
        label: 'Ïõî Îß§Ï∂ú',
        data: [results.case1.ÏõîÎß§Ï∂ú, results.case2.ÏõîÎß§Ï∂ú, results.case3.ÏõîÎß§Ï∂ú],
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
