    ],
  };

  const combinedChartData = {
    labels: ['Í∏∞Î≥∏', `${scenarios.case2}% Ï¶ùÍ∞Ä`, `${scenarios.case3}% Ï¶ùÍ∞Ä`],
    datasets: [
      {
        type: 'bar',
        label: 'Ïõî Îß§Ï∂ú',
        data: [results.case1.ÏõîÎß§Ï∂ú, results.case2.ÏõîÎß§Ï∂ú, results.case3.ÏõîÎß§Ï∂ú],
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
        yAxisID: 'y',
      },
      {
        type: 'bar',
        label: 'Ïõî Ï¥ù ÏßÄÏ∂ú',
        data: [results.case1.ÏõîÏ¥ùÏßÄÏ∂ú, results.case2.ÏõîÏ¥ùÏßÄÏ∂ú, results.case3.ÏõîÏ¥ùÏßÄÏ∂ú],
        backgroundColor: 'rgba(255, 99, 132, 0.6)',
        yAxisID: 'y',
      },
      {
        type: 'line',
        label: 'Ïõî ÏàúÏù¥Ïùµ',
        data: [
          results.case1.ÏõîÎß§Ï∂ú - results.case1.ÏõîÏ¥ùÏßÄÏ∂ú,
          results.case2.ÏõîÎß§Ï∂ú - results.case2.ÏõîÏ¥ùÏßÄÏ∂ú,
          results.case3.ÏõîÎß§Ï∂ú - results.case3.ÏõîÏ¥ùÏßÄÏ∂ú,
