                                {index === 0 ? 'Í∏∞Î≥∏' : `${scenarios[case_]}% Ï¶ùÍ∞Ä`}
                              </Typography>
                              <Typography sx={{ color: '#1d1d1f', fontSize: '1.1rem', mt: 0.5 }}>
                                {years}ÎÖÑ {months}Í∞úÏõî {emoji}
                              </Typography>
                            </Paper>
                          </Grid>
                        );
                      })}
                    </Grid>
                  </Box>
                </Paper>
              </Grid>
            </Grid>
          </Grid>
        </Grid>
      </div>
    </Container>
  );
}

export default App;
