function App() {
  const [inputs, setInputs] = useState({
    ÌèâÎã®Í∞Ä: 3000,
    ÏùºÎß§Ï∂úÏàò: 50,
    ÏõêÏû¨Î£åÎπÑ: 800,
    ÏõîÍ¥ÄÎ¶¨ÎπÑ: 340000,
    ÏÉÅÍ∞ÄÎ≥¥Ï¶ùÍ∏à: 30000000,
    ÎåÄÏ∂úÏù¥Ïûê: 5,
    Ìà¨ÏûêÎπÑ: 65000000,
  });

  const [scenarios, setScenarios] = useState({
    case2: 10,
    case3: 20,
  });

  const [results, setResults] = useState({
    case1: {},
    case2: {},
    case3: {},
