  const contentRef = useRef(null);

  const formatNumber = (num) => {
    return num.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
  };

  const getEmoji = (years, months) => {
    const totalMonths = years * 12 + months;
    const targetMonths = 24; // 2ÎÖÑ
    const threshold = targetMonths * 0.2; // 20% Í∏∞Ï§Ä

    if (totalMonths < targetMonths) {
      return 'üòÑ'; // 2ÎÖÑ ÎØ∏Îßå
    } else if (Math.abs(totalMonths - targetMonths) <= threshold) {
      return 'üôÇ'; // 2ÎÖÑ ¬±20% ÎÇ¥
    } else {
      return 'üò¢'; // 2ÎÖÑÎ≥¥Îã§ 20% Ï¥àÍ≥º
    }
  };

  const calculateResults = () => {
    // Case 1 (Í∏∞Î≥∏)
    const case1 = {
      ÏõîÎß§Ï∂ú: inputs.ÌèâÎã®Í∞Ä * ((inputs.ÏùºÎß§Ï∂úÏàò * 22) + (inputs.ÏùºÎß§Ï∂úÏàò * 1.1 * 8)),
      ÏõîÏ¥ùÏßÄÏ∂ú: (inputs.ÏõêÏû¨Î£åÎπÑ * ((inputs.ÏùºÎß§Ï∂úÏàò * 22) + (inputs.ÏùºÎß§Ï∂úÏàò * 1.1 * 8))) +
        inputs.ÏõîÍ¥ÄÎ¶¨ÎπÑ + (inputs.ÏÉÅÍ∞ÄÎ≥¥Ï¶ùÍ∏à * (inputs.ÎåÄÏ∂úÏù¥Ïûê / 100) / 12),
    };

    // Case 2 (ÏÇ¨Ïö©Ïûê ÏßÄÏ†ï % Ï¶ùÍ∞Ä)
    const case2 = {
      ÏõîÎß§Ï∂ú: inputs.ÌèâÎã®Í∞Ä * ((inputs.ÏùºÎß§Ï∂úÏàò * (1 + scenarios.case2/100) * 22) + 
        (inputs.ÏùºÎß§Ï∂úÏàò * (1 + scenarios.case2/100) * 1.1 * 8)),
      ÏõîÏ¥ùÏßÄÏ∂ú: (inputs.ÏõêÏû¨Î£åÎπÑ * ((inputs.ÏùºÎß§Ï∂úÏàò * (1 + scenarios.case2/100) * 22) + 
        (inputs.ÏùºÎß§Ï∂úÏàò * (1 + scenarios.case2/100) * 1.1 * 8))) +
        inputs.ÏõîÍ¥ÄÎ¶¨ÎπÑ + (inputs.ÏÉÅÍ∞ÄÎ≥¥Ï¶ùÍ∏à * (inputs.ÎåÄÏ∂úÏù¥Ïûê / 100) / 12),
    };

    // Case 3 (ÏÇ¨Ïö©Ïûê ÏßÄÏ†ï % Ï¶ùÍ∞Ä)
    const case3 = {
      ÏõîÎß§Ï∂ú: inputs.ÌèâÎã®Í∞Ä * ((inputs.ÏùºÎß§Ï∂úÏàò * (1 + scenarios.case3/100) * 22) + 
        (inputs.ÏùºÎß§Ï∂úÏàò * (1 + scenarios.case3/100) * 1.1 * 8)),
      ÏõîÏ¥ùÏßÄÏ∂ú: (inputs.ÏõêÏû¨Î£åÎπÑ * ((inputs.ÏùºÎß§Ï∂úÏàò * (1 + scenarios.case3/100) * 22) + 
        (inputs.ÏùºÎß§Ï∂úÏàò * (1 + scenarios.case3/100) * 1.1 * 8))) +
        inputs.ÏõîÍ¥ÄÎ¶¨ÎπÑ + (inputs.ÏÉÅÍ∞ÄÎ≥¥Ï¶ùÍ∏à * (inputs.ÎåÄÏ∂úÏù¥Ïûê / 100) / 12),
    };

    setResults({ case1, case2, case3 });
  };

  useEffect(() => {
    calculateResults();
  }, [inputs, scenarios]);

  const handleInputChange = (field) => (event) => {
    const value = event.target.value === '' ? '' : Number(event.target.value);
    setInputs({
      ...inputs,
      [field]: value,
    });
  };

  const handleScenarioChange = (field) => (event, newValue) => {
    setScenarios({
      ...scenarios,
      [field]: newValue,
    });
  };

  const exportToPDF = async () => {
    if (contentRef.current) {
      const canvas = await html2canvas(contentRef.current);
      const imgData = canvas.toDataURL('image/png');
      const pdf = new jsPDF('p', 'mm', 'a4');
      const pdfWidth = pdf.internal.pageSize.getWidth();
      const pdfHeight = (canvas.height * pdfWidth) / canvas.width;
      
      pdf.addImage(imgData, 'PNG', 0, 0, pdfWidth, pdfHeight);
      pdf.save('ÎπÑÏ¶àÎãàÏä§_ÏàòÏùµÏÑ±_Î∂ÑÏÑù.pdf');
    }
  };

  const barChartData = {
    labels: ['Í∏∞Î≥∏', `${scenarios.case2}% Ï¶ùÍ∞Ä`, `${scenarios.case3}% Ï¶ùÍ∞Ä`],
    datasets: [
      {
        label: 'Ïõî Îß§Ï∂ú',
        data: [results.case1.ÏõîÎß§Ï∂ú, results.case2.ÏõîÎß§Ï∂ú, results.case3.ÏõîÎß§Ï∂ú],
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
      },
      {
        label: 'Ïõî Ï¥ù ÏßÄÏ∂ú',
        data: [results.case1.ÏõîÏ¥ùÏßÄÏ∂ú, results.case2.ÏõîÏ¥ùÏßÄÏ∂ú, results.case3.ÏõîÏ¥ùÏßÄÏ∂ú],
        backgroundColor: 'rgba(255, 99, 132, 0.6)',
      },
    ],
  };

  const lineChartData = {
    labels: ['Í∏∞Î≥∏', `${scenarios.case2}% Ï¶ùÍ∞Ä`, `${scenarios.case3}% Ï¶ùÍ∞Ä`],
    datasets: [
      {
        label: 'Ïõî ÏàúÏù¥Ïùµ',
        data: [
          results.case1.ÏõîÎß§Ï∂ú - results.case1.ÏõîÏ¥ùÏßÄÏ∂ú,
          results.case2.ÏõîÎß§Ï∂ú - results.case2.ÏõîÏ¥ùÏßÄÏ∂ú,
          results.case3.ÏõîÎß§Ï∂ú - results.case3.ÏõîÏ¥ùÏßÄÏ∂ú,
        ],
        borderColor: 'rgb(75, 192, 192)',
        tension: 0.1,
      },
    ],
  };

  const combinedChartData = {
    labels: ['Í∏∞Î≥∏', `${scenarios.case2}% Ï¶ùÍ∞Ä`, `${scenarios.case3}% Ï¶ùÍ∞Ä`],
    datasets: [
      {
        type: 'bar',
        label: 'Ïõî Îß§Ï∂ú',
        data: [results.case1.ÏõîÎß§Ï∂ú, results.case2.ÏõîÎß§Ï∂ú, results.case3.ÏõîÎß§Ï∂ú],
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
        yAxisID: 'y',
      },
      {
        type: 'bar',
        label: 'Ïõî Ï¥ù ÏßÄÏ∂ú',
        data: [results.case1.ÏõîÏ¥ùÏßÄÏ∂ú, results.case2.ÏõîÏ¥ùÏßÄÏ∂ú, results.case3.ÏõîÏ¥ùÏßÄÏ∂ú],
        backgroundColor: 'rgba(255, 99, 132, 0.6)',
        yAxisID: 'y',
      },
      {
        type: 'line',
        label: 'Ïõî ÏàúÏù¥Ïùµ',
        data: [
          results.case1.ÏõîÎß§Ï∂ú - results.case1.ÏõîÏ¥ùÏßÄÏ∂ú,
          results.case2.ÏõîÎß§Ï∂ú - results.case2.ÏõîÏ¥ùÏßÄÏ∂ú,
          results.case3.ÏõîÎß§Ï∂ú - results.case3.ÏõîÏ¥ùÏßÄÏ∂ú,
        ],
        borderColor: 'rgb(153, 102, 255)',
        tension: 0.1,
        yAxisID: 'y1',
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
    },
    scales: {
      y: {
        beginAtZero: true,
        grid: {
          color: 'rgba(0, 0, 0, 0.1)',
        },
      },
      x: {
        grid: {
          display: false,
        },
      },
    },
  };

  const renderInputSection = (title, fields) => (
    <Paper 
      sx={{ 
        p: 3, 
        mb: 2, 
        borderRadius: 2,
        boxShadow: '0 2px 12px rgba(0,0,0,0.08)',
        backgroundColor: 'rgba(255, 255, 255, 0.9)',
      }}
    >
      <Typography variant="h6" gutterBottom sx={{ color: '#1d1d1f', fontWeight: 500 }}>
        {title}
      </Typography>
      <Grid container spacing={2}>
        {fields.map((field) => (
          <Grid item xs={12} sm={6} key={field}>
            <TextField
              fullWidth
              label={field}
              type="number"
              value={inputs[field]}
              onChange={handleInputChange(field)}
              variant="outlined"
              size="small"
              inputProps={{ inputMode: 'numeric', pattern: '[0-9]*' }}
              InputProps={{
                endAdornment: field === 'ÎåÄÏ∂úÏù¥Ïûê' ? '%' : null,
                readOnly: false,
                sx: {
                  '& .MuiOutlinedInput-notchedOutline': {
                    borderColor: 'rgba(0, 0, 0, 0.1)',
                  },
                  '&:hover .MuiOutlinedInput-notchedOutline': {
                    borderColor: 'rgba(0, 0, 0, 0.2)',
                  },
                },
              }}
              InputLabelProps={{
                shrink: true,
                sx: { color: '#1d1d1f' },
              }}
              FormHelperTextProps={{
                sx: { textAlign: 'right', color: '#86868b' }
              }}
              helperText={field !== 'ÎåÄÏ∂úÏù¥Ïûê' ? formatNumber(inputs[field]) : null}
            />
          </Grid>
        ))}
      </Grid>
    </Paper>
  );

  return (
    <Container maxWidth="xl" sx={{ mt: 2, mb: 2, height: '100vh' }}>
      <Typography 
        variant="h4" 
        component="h1" 
        gutterBottom 
        align="center"
        sx={{ 
          color: '#1d1d1f',
          fontWeight: 600,
          mb: 2,
        }}
      >
        ÎπÑÏ¶àÎãàÏä§ ÏàòÏùµÏÑ± Î∂ÑÏÑù
      </Typography>

      <Box sx={{ mb: 2, textAlign: 'right' }}>
        <Button 
          variant="contained" 
          onClick={exportToPDF}
          sx={{
            backgroundColor: '#0071e3',
            '&:hover': {
              backgroundColor: '#0077ed',
            },
            borderRadius: 2,
            textTransform: 'none',
            px: 3,
          }}
