                  </Typography>
                  <Slider
                    value={scenarios.case2}
                    onChange={handleScenarioChange('case2')}
                    min={0}
                    max={50}
                    step={1}
                    valueLabelDisplay="auto"
                    sx={{
                      color: '#0071e3',
                      '& .MuiSlider-thumb': {
                        width: 12,
                        height: 12,
                      },
                    }}
                  />
                </Grid>
                <Grid item xs={12}>
                  <Typography gutterBottom sx={{ color: '#1d1d1f' }}>
                    Case 3 Ï¶ùÍ∞ÄÏú®: {scenarios.case3}%
                  </Typography>
                  <Slider
                    value={scenarios.case3}
                    onChange={handleScenarioChange('case3')}
                    min={0}
                    max={50}
                    step={1}
                    valueLabelDisplay="auto"
                    sx={{
                      color: '#0071e3',
                      '& .MuiSlider-thumb': {
                        width: 12,
                        height: 12,
                      },
                    }}
                  />
                </Grid>
              </Grid>

              {renderInputSection('Îß§Ï∂ú ÏûÖÎ†•Í∞í', ['ÌèâÎã®Í∞Ä', 'ÏùºÎß§Ï∂úÏàò', 'ÏõêÏû¨Î£åÎπÑ'])}
              {renderInputSection('ÏõîÍ¥ÄÎ¶¨ÎπÑ', ['ÏõîÍ¥ÄÎ¶¨ÎπÑ'])}
              {renderInputSection('ÏÉÅÍ∞ÄÏûÑÎåÄÎ£å', ['ÏÉÅÍ∞ÄÎ≥¥Ï¶ùÍ∏à', 'ÎåÄÏ∂úÏù¥Ïûê'])}
              {renderInputSection('Ìà¨ÏûêÎπÑ', ['Ìà¨ÏûêÎπÑ'])}
            </Paper>
          </Grid>

          {/* Right Side - Results Section */}
          <Grid item xs={12} md={7}>
            <Grid container spacing={2} sx={{ height: '100%' }}>
              {/* Charts */}
              <Grid item xs={12} sx={{ height: '70%' }}>
                <Paper 
                  sx={{ 
                    p: 2,
                    height: '100%',
                    borderRadius: 2,
                    boxShadow: '0 2px 12px rgba(0,0,0,0.08)',
                    backgroundColor: 'rgba(255, 255, 255, 0.9)',
                    overflow: 'auto',
                  }}
                >
                  <Typography variant="h6" gutterBottom sx={{ color: '#1d1d1f', fontWeight: 500 }}>
                    Í≤∞Í≥º Î∂ÑÏÑù
                  </Typography>
                  <Box sx={{ mb: 2 }}>
                    <Typography variant="subtitle1" sx={{ color: '#1d1d1f', mb: 1 }}>
                      Ïõî Îß§Ï∂ú Î∞è ÏßÄÏ∂ú ÎπÑÍµê
                    </Typography>
                    <Bar data={barChartData} options={chartOptions} />
                  </Box>
                  <Box sx={{ mb: 2 }}>
                    <Typography variant="subtitle1" sx={{ color: '#1d1d1f', mb: 1 }}>
                      Ïõî ÏàúÏù¥Ïùµ Ï∂îÏù¥
                    </Typography>
                    <Line data={lineChartData} options={chartOptions} />
                  </Box>
                  <Box>
                    <Typography variant="subtitle1" sx={{ color: '#1d1d1f', mb: 1 }}>
                      ÌÜµÌï© Î∂ÑÏÑù
                    </Typography>
