import React, { useState, useEffect } from 'react';
import { Container, Grid, Paper, Typography, TextField, Box } from '@mui/material';
import { Bar, Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  Title,
  Tooltip,
  Legend
);

function App() {
  const [inputs, setInputs] = useState({
    평단가: 3000,
    일매출수: 50,
    원재료비: 800,
    상가임대료: 1000000,
    상가보증금: 30000000,
    대출이자: 0.05,
    머신: 35000000,
    인테리어: 20000000,
    가터투자비: 10000000,
    머신관리비: 50000,
    기타관리비: 50000,
    CCTV및음향: 50000,
    수도광열비: 200000,
    인터넷: 40000,
  });

  const [results, setResults] = useState({
    case1: {},
    case2: {},
    case3: {},
  });

  const calculateResults = () => {
    // Case 1 (기본)
    const case1 = {
      월매출: inputs.평단가 * ((inputs.일매출수 * 22) + (inputs.일매출수 * 1.1 * 8)),
      월총지출: (inputs.원재료비 * ((inputs.일매출수 * 22) + (inputs.일매출수 * 1.1 * 8))) +
        inputs.머신관리비 + inputs.기타관리비 + inputs.CCTV및음향 + inputs.수도광열비 + inputs.인터넷 +
        inputs.상가임대료 + (inputs.상가보증금 * inputs.대출이자 / 12),
    };

    // Case 2 (10% 증가)
    const case2 = {
      월매출: inputs.평단가 * ((inputs.일매출수 * 1.1 * 22) + (inputs.일매출수 * 1.1 * 1.1 * 8)),
      월총지출: (inputs.원재료비 * ((inputs.일매출수 * 1.1 * 22) + (inputs.일매출수 * 1.1 * 1.1 * 8))) +
        inputs.머신관리비 + inputs.기타관리비 + inputs.CCTV및음향 + inputs.수도광열비 + inputs.인터넷 +
        inputs.상가임대료 + (inputs.상가보증금 * inputs.대출이자 / 12),
    };

    // Case 3 (20% 증가)
    const case3 = {
      월매출: inputs.평단가 * ((inputs.일매출수 * 1.2 * 22) + (inputs.일매출수 * 1.2 * 1.1 * 8)),
      월총지출: (inputs.원재료비 * ((inputs.일매출수 * 1.2 * 22) + (inputs.일매출수 * 1.2 * 1.1 * 8))) +
        inputs.머신관리비 + inputs.기타관리비 + inputs.CCTV및음향 + inputs.수도광열비 + inputs.인터넷 +
        inputs.상가임대료 + (inputs.상가보증금 * inputs.대출이자 / 12),
    };

    setResults({ case1, case2, case3 });
  };

  useEffect(() => {
    calculateResults();
  }, [inputs]);

  const handleInputChange = (field) => (event) => {
    setInputs({
      ...inputs,
      [field]: Number(event.target.value),
    });
  };

  const barChartData = {
    labels: ['Case 1', 'Case 2', 'Case 3'],
    datasets: [
      {
        label: '월 매출',
        data: [results.case1.월매출, results.case2.월매출, results.case3.월매출],
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
      },
      {
        label: '월 총 지출',
        data: [results.case1.월총지출, results.case2.월총지출, results.case3.월총지출],
        backgroundColor: 'rgba(255, 99, 132, 0.6)',
      },
    ],
  };

  const lineChartData = {
    labels: ['Case 1', 'Case 2', 'Case 3'],
    datasets: [
      {
        label: '월 순이익',
        data: [
          results.case1.월매출 - results.case1.월총지출,
          results.case2.월매출 - results.case2.월총지출,
          results.case3.월매출 - results.case3.월총지출,
        ],
        borderColor: 'rgb(75, 192, 192)',
        tension: 0.1,
      },
    ],
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Typography variant="h4" component="h1" gutterBottom align="center">
        비즈니스 수익성 분석
      </Typography>

      <Grid container spacing={3}>
        <Grid item xs={12} md={6}>
          <Paper sx={{ p: 2 }}>
            <Typography variant="h6" gutterBottom>
              입력값
            </Typography>
            <Grid container spacing={2}>
              {Object.entries(inputs).map(([key, value]) => (
                <Grid item xs={12} sm={6} key={key}>
                  <TextField
                    fullWidth
                    label={key}
                    type="number"
                    value={value}
                    onChange={handleInputChange(key)}
                    variant="outlined"
                    size="small"
                  />
                </Grid>
              ))}
            </Grid>
          </Paper>
        </Grid>

        <Grid item xs={12} md={6}>
          <Paper sx={{ p: 2 }}>
            <Typography variant="h6" gutterBottom>
              결과
            </Typography>
            <Box sx={{ mb: 2 }}>
              <Typography variant="subtitle1">월 매출 및 지출 비교</Typography>
              <Bar data={barChartData} />
            </Box>
            <Box>
              <Typography variant="subtitle1">월 순이익 추이</Typography>
              <Line data={lineChartData} />
            </Box>
          </Paper>
        </Grid>

        <Grid item xs={12}>
          <Paper sx={{ p: 2 }}>
            <Typography variant="h6" gutterBottom>
              투자 회수 기간
            </Typography>
            <Grid container spacing={2}>
              {['case1', 'case2', 'case3'].map((case_) => {
                const totalInvestment = inputs.머신 + inputs.인테리어 + inputs.가터투자비;
                const monthlyProfit = results[case_].월매출 - results[case_].월총지출;
                const monthsToRecover = totalInvestment / monthlyProfit;
                const years = Math.floor(monthsToRecover / 12);
                const months = Math.round(monthsToRecover % 12);

                return (
                  <Grid item xs={12} sm={4} key={case_}>
                    <Paper sx={{ p: 2, bgcolor: 'background.default' }}>
                      <Typography variant="subtitle1">
                        {case_.toUpperCase()}
                      </Typography>
                      <Typography>
                        {years}년 {months}개월
                      </Typography>
                    </Paper>
                  </Grid>
                );
              })}
            </Grid>
          </Paper>
        </Grid>
      </Grid>
    </Container>
  );
}

export default App;
